theme_opts.create_style:
pre, pre * {font-family: monospace !important;} .tooltip * {color: white !important}

nav {
  background: linear-gradient(to right, #088, #0ca) !important;
  padding: 10px !important;
}
nav, nav * {
  box-sizing: border-box !important;
}
nav * {
  color: white !important;
}
div.search-box-input-container {
  background: #fff4 !important;
  border-radius: 5px !important;
}
nav ::placeholder {
  color: #ddd !important;
}
nav svg {
  fill: white !important;
}
.methods {
  background: #0001 !important;
}
.accordion-content {
  background: #fff1 !important;
}
div.accordion-heading {
  background: #fff3 !important;
}

::-webkit-scrollbar {
  width: 3px !important;
}
::-webkit-scrollbar-track{
  background: #0bb1 !important;
}

nav ::-webkit-scrollbar-thumb{
  border-radius: 1000px !important;
  background: #fff3 !important;
}

.page-title, header * {
  border-color: #0ca5;
}
article pre * {
  background: #111;
}

li.p-h-n {
  color: #333 !important;
}
#search-item-ul * {
  color: #333 !important;
}
#search-item-ul {
  background: #fff9 !important;
  backdrop-filter: blur(10px) !important;
  border-radius: 3px !important;
}

div.code-copy-icon-container:hover * {
  color: white !important;
  fill: white !important;
}

theme_opts.add_scripts:
setInterval(() => {
  try {
    document.querySelector('.os-content-arrange').remove();
  } catch (e) {}

  var els = Array.from(document.querySelectorAll('ul.accordion-content a')).filter(el => el.innerText.startsWith("exports."));
  for (var i = 0; i < els.length; i++) {
    els[i].innerText = els[i].innerText.replace(/^exports\./, '');
  }
}, 100);
